const testWrapper=document.querySelector(".test-wrapper");const testArea=document.querySelector("#test-area");const originText=document.querySelector("#origin-text p").innerHTML;const resetButton=document.querySelector("#reset");const theTimer=document.querySelector(".timer");const timerContainer=document.querySelector("#clock .timer");var timerRunning;var my_interval=false;let timer=[0,0,0,0];function leadingZero(time){if(timer<=9){time=`0 ${time}`}timer}function flashTimer(){timerContainer.classList.toggle(".hide")}function runTimer(){let currentTimer=`${timer[0]}:${timer[1]}:${timer[2]}`;theTimer.innerHTML=currentTimer;timer[3]++;timer[0]=Math.floor(timer[3]/100/60);timer[1]=Math.floor(timer[3]/100-timer[0]*60);timer[2]=Math.floor(timer[3]-timer[1]*100-timer[0]*6e3)}function spellCheck(){let textEntered=testArea.value;let originTextMatch=originText.substring(0,textEntered.length);if(textEntered==originText){clearInterval(my_interval);testWrapper.style.borderColor="#429890"}else if(textEntered==originTextMatch){testWrapper.style.borderColor="#65CCf3"}else{testWrapper.style.borderColor="#E95D0F"}}function start(){let text_entered_length=testArea.value.length;if(text_entered_length===0&&!timerRunning){timerRunning=true;my_interval=setInterval(runTimer,10);setInterval(flashTimer,10)}console.log(text_entered_length)}function reset(){clearInterval(my_interval);my_interval=null;timer=[0,0,0,0];timerRunning=false;testArea.value="";theTimer.innerHTML="00:00:00";testWrapper.style.cssText="border: 10px solid grey; display: flex; justify-content: center; align-items: center; text-align: center;"}testArea.addEventListener("keypress",start,false);testArea.addEventListener("keyup",spellCheck,false);resetButton.addEventListener("click",(function(){reset()}),false);